import { Component, HostListener, OnInit } from "@angular/core";
import { Router } from "@angular/router";
import { NbDialogService, NbMenuItem } from "@nebular/theme";
import { CapitalizadoraService } from "src/app/service/capitalizadora.service";
import { BackdropClickDialogComponent } from "../backdrop-click-dialog/backdrop-click-dialog.component";

@Component({
  selector: "app-header",
  templateUrl: "./header.component.html",
  styleUrls: ["./header.component.scss"],
})
export class HeaderComponent {
  // Itens do menu
  menuItems: NbMenuItem[] = [
    {
      title: "inicio",
      link: "/inicio",
      selected: window.location.pathname === "/inicio",
    },
    {
      title: "Movimentação",
      link: "/movimentacao",
      selected: window.location.pathname === "/movimentacao",
    },
  ];

  aplicap =
    "data:image/jpeg;base64,";

  capemisa =
    "data:image/png;base64,";

  kovr =
    "data:image/png;base64,";

  viacap =
    "data:image/jpg;base64,";

  base64 = "";
  constructor(
    private router: Router,
    private capitalizadoraService: CapitalizadoraService,
    private dialogService: NbDialogService
  ) {}

  ngOnInit() {
    if (!localStorage.getItem("nome_usuario")) {
      this.router.navigate(["/login"]); // Redireciona para a página de login
    }

    this.router.events.subscribe((val) => {
      this.menuItems.forEach((item) => {
        item.selected = this.router.url.includes(item.link || "");
      });
    });

    this.capitalizadoraService.capitalizadora$.subscribe((cap) => {
      const capLower = cap.toLowerCase();
      console.log(capLower);

      if (capLower.includes("kovr")) {
        this.base64 = this.kovr;
      } else if (capLower.includes("capemisa")) {
        this.base64 = this.capemisa;
      } else if (capLower.includes("aplicap")) {
        this.base64 = this.aplicap;
      } else if (capLower.includes("via capitalizacao")) {
        this.base64 = this.viacap;
      } else {
        this.base64 = "";
      }
    });
  }

  logout() {
    // Aqui você pode limpar os dados do usuário, tokens, etc.
    console.log("Usuário saiu");
    localStorage.removeItem("nome_usuario");
    this.router.navigate(["/login"]); // Redireciona para a página de login
  }

  openModal() {
    this.open(true);
  }

  protected open(closeOnBackdropClick: boolean) {
    this.dialogService.open(BackdropClickDialogComponent, {
      closeOnBackdropClick,
    });
  }
}
